mapper.select:
select id, coach_name, family_name, first_name, school_id, phone, email, sex, size, check_status
from coach
where school_id = #{schoolId}

select retrieve_code
from coach
where email = #{email}

mapper.update:
update coach
coach_name =#{coachName}, family_name =#{familyName}, first_name =#{firstName}, email =#{email}, size =#{size}, sex =#{sex},
where id = #{id}

update coach
set retrieve_code = #{code}
where email = #{email}

update coach
set pswd = #{pswd}
where id = #{id}

update coach
set check_status = #{status}
where id = #{id}
