mapper.select:
select * from solo_contest
where id in (
select solo_contest_id from solo_contest_record where student_id = #{studentId}
)

mapper.update:
update solo_contest
title = #{title}, start_time=#{startTime}, end_time=#{endTime}, sign_start_time=#{signStartTime}, sign_end_time=#{signEndTime}, school_id=#{schoolId}, chief=#{chief}, email=#{email}, phone=#{phone}, num_max=#{numMax},
where id = #{id}

update solo_contest
set num_fact = #{numFact}
where id = #{id}

mapper.select:
select student_id
from solo_contest_record
where solo_contest_id = #{soloContestId} and student_id = #{studentId}

select COUNT(Z.student_id)
from solo_contest_record as Z
where Z.solo_contest_id = #{soloContestId}
