mapper.select:
select retrieve_code
from student
where email = #{email}

mapper.update:
update student
stu_id = #{stuId}, family_name = #{familyName}, first_name=#{firstName}, college=#{college}, major=#{major}, enroll_year=#{enrollYear}, email=#{email}, size=#{size}, sex=#{sex},
where id = #{id}

update student
set retrieve_code = #{code}
where email = #{email}

update student
set pswd = #{pswd}
where id = #{id}

update student
set check_status = #{status}
where id = #{id}
